import React, { useState } from "react";
import { useSelector } from "react-redux";
import { NavLink } from "react-router-dom";
import { Button } from "../../UI/Button/Button";
import { Input } from "../../UI/Input/Input";
import classes from './Registration.module.scss'


function Registration () {

    const state = useSelector(store => store.users)

    function addUserToBase(){
        let user = {
            name: loginValue,
            password: passwordValue,
            id: 5
        }
        state.push(user)
    }

    console.log(state);

    const [loginValue, loginHandler] = useState('')
    const [passwordValue, passwordHandler] = useState('')
    const [doublePassword, doublePasswordHandler] = useState('')


    return(
        <div className={classes.conteiner}>

            <h3>Регистрация</h3>
            <p>Заполните все поля</p>

        <div className={classes.form}>
            <form>
                <Input onChange={(event) => loginHandler(event.target.value)} type='text' id='login' ph='Введите ваш логин' label='Логин' value={loginValue} />
                <Input onChange={(event) => passwordHandler(event.target.value)} type='password' id='pass' ph='Введите ваш пароль' label='Пароль' value={passwordValue} />
                <Input onChange={(event) => doublePasswordHandler(event.target.value)} type='password' id='doublepass' ph='Повторите ваш пароль' label='Повторите пароль' value={doublePassword} />

                <div className={classes.btnLine}>
                <Button onClick={() => addUserToBase(console.log('LOH'))} name='Зарегестрироваться' />
                </div>
            </form>
        </div>

        </div>
    )
}

export default Registration
