import React, { useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { removeToDo, toggleToDo } from "../../Redux/createActions";
import { Button } from "../../UI/Button/Button";
import { Input } from "../../UI/Input/Input";
import classes from './List.module.scss'


function TaskList() {

    const user = useSelector(store => store.userData.userOnline)
    const allTodos = useSelector(store => store.taskData.todos) 
    const dispatch = useDispatch()

    const [value, setValue] = useState('')

    const myTodos = allTodos.filter(item => item.userID === user.id)
    // console.log('user', myTodos);


    function addTaskHandler() {
        const newTask = {
            text: value,
            userID: user.userID,
            taskID: allTodos.length + 1,
            completed: false,
            cat: 'Все задачи'
        }

        dispatch()

        setValue('')
    }



    return(
        <div className={classes.TaskList}>
            <div>
        {
            myTodos.length
            ? myTodos.map(task => {
            return(
            <label onChange={(e) => dispatch({type: toggleToDo(), payload: myTodos.find(el => el.taskID === Number(e.target.id))})} key={task.taskID}>
                <div className={classes.task}>
                <input id={task.taskID} type='checkbox' /> 
                <p className={task.completed ? classes.done : null}>{task.text}</p>
                </div>
                <div className={classes.delete} id={task.taskID} onClick={e => dispatch({type: removeToDo(), payload: myTodos.find(el => el.taskID === Number(e.target.id))})}>
                     &#10006; {/*<span id={task.taskID} onClick={e => dispatch({type: removeToDo(), payload: myTodos.find(el => el.taskID === Number(e.target.id))})}>удалить</span> */}
                </div>
            </label>
            )
                })
            : <h3>Нет задач</h3>
        }
        </div>


            <div className={classes.row_input}>
                <input 
                type="text" 
                className="form-control" 
                placeholder="Добавить задачу" 
                value={value}
                onChange={(e) => setValue(e.target.value)}
                />

                <Button 
                name='добавить'
                onClick={() => addTaskHandler}
                />
            </div>


        </div>
    )
}

export default TaskList
