import { createStore } from "redux";
import { ADD_TODO, TOGGLE_TODO, REMOVE_TODO, ADD_USER } from './actions'


const initialState = {
    categories: ['All task'],
    userOnline: {
        name: '',
        id: '',
    },
    users: [
        {
         name: 'admin',
         password: 'admin',
         id: 1
        }
    ],
    todos: [
        {
            text: 'Test todo',
            completed: false,
            cat: 'All task'
        }
    ],
}




const reducer = (state = initialState, action) => {
    switch(action.type){
        case ADD_USER:
            return {}


        default:
            return state
    }
}


export const store = createStore(reducer, window.__REDUX_DEVTOOLS_EXTENSION__ && window.__REDUX_DEVTOOLS_EXTENSION__())
