import React, { useState } from "react";

export const Input = (props) => {



    const {inputValue, inputHandler} = useState('')

    return (
        <div>
            <label htmlFor={props.id} className="form-label">{props.label}</label>
            <input onChange={() => inputHandler(console.log(event.target.value))} type={props.type} className="form-control" value={inputValue} id={props.id} aria-describedby="emailHelp" placeholder={props.ph} />
        </div>
    )
}
