import { combineReducers } from "redux";
import { ADD_USER, EXIT_USER, LOGIN_USER, TOGGLE_TODO } from "./actions";


const initialStateUsers = {
    users: [
        {
         name: 'admin',
         password: 'admin',
         id: 1,
         categories: ['Все задачи']
        },
        {
            name: '1',
            password: '1',
            id: 2,
            categories: ['Все задачи'],
        }
    ]
}

const initialStateTasks = [
        {
            text: 'Test todo from state',
            filterID: 2,
            completed: false,
            cat: 'Все задачи'
        }
    ]


const taskReducer = (state = initialStateTasks, action) => {
    switch (action.type){
        case TOGGLE_TODO:
            // const task = state.filter((el) => el.filterID === action.payload)
            console.log(state);
            console.log(task);
            return {
                ...state,

            }


        default:
            return state
    }

}



const userReducer = (state = initialStateUsers, action) => {
    switch (action.type){
        case ADD_USER:
            return {
                ...state,
                userOnline: action.payload,
                users: [...state.users, action.payload]
            }

        case LOGIN_USER:
            return {
                ...state,
                userOnline: action.payload
            }

        case EXIT_USER:
            return {
                ...state,
                userOnline: null
            }




        default:
            return state
    }
}


export const rootReducer = combineReducers({taskData: taskReducer, userData: userReducer})
