import { combineReducers } from "redux";
import { ADD_USER, EXIT_USER, LOGIN_USER, TOGGLE_TODO } from "./actions";


const initialStateUsers = {
    users: [
        {
         name: 'admin',
         password: 'admin',
         id: 1,
         categories: ['Все задачи']
        },
        {
            name: '1',
            password: '1',
            id: 2,
            categories: ['Все задачи'],
        }
    ]
}

const initialStateTasks = {
    todos: [
        {
            text: 'Tst todo from state',
            userID: 2,
            taskID: 1,
            completed: false,
            cat: 'Все задачи'
        },
        {
            text: 'Test tdo from state',
            userID: 2,
            taskID: 12,
            completed: false,
            cat: 'Все задачи'
        },
        {
            text: 'Test todo frm state',
            userID: 2,
            taskID: 18,
            completed: false,
            cat: 'Все задачи'
        },
        {
            text: 'Test todo from sate',
            userID: 2,
            taskID: 112,
            completed: false,
            cat: 'Все задачи'
        },
    ]
}

const taskReducer = (state = initialStateTasks, action) => {
    switch (action.type){
        case TOGGLE_TODO:
            const test = [...state.todos].find((el) => (el.taskID === action.payload.taskID))
            console.log(test);
            test.completed = !test.completed

            return {
                ...state,
                todos: [...state.todos]

            }


        default:
            return state
    }

}



const userReducer = (state = initialStateUsers, action) => {
    switch (action.type){
        case ADD_USER:
            return {
                ...state,
                userOnline: action.payload,
                users: [...state.users, action.payload]
            }

        case LOGIN_USER:
            return {
                ...state,
                userOnline: action.payload
            }

        case EXIT_USER:
            return {
                ...state,
                userOnline: null
            }




        default:
            return state
    }
}


export const rootReducer = combineReducers({taskData: taskReducer, userData: userReducer})
