import React from "react";
import { useDispatch, useSelector } from "react-redux";
import { toggleToDo } from "../../Redux/createActions";
import classes from './List.module.scss'


function TaskList() {

    const user = useSelector(store => store.userData.userOnline)
    const allTodos = useSelector(store => store.taskData.todos) 
    const dispatch = useDispatch()

    const myTodos = allTodos.filter(item => item.filterID === user.id)

    console.log('from tasklist state', allTodos);
    // console.log(user);

    return(
        <div>
        {
            myTodos.length
            ? myTodos.map(task => {
            return(
            <label onChange={(e) => dispatch({type: toggleToDo(), payload: e.target.id})} key={task.filterID}>
                <input id={task.filterID} type='checkbox' /> 
                <p className=''>{task.text}</p>
            </label>
            )
                })
            : <h3>Нет задач</h3>
        }





        </div>
    )
}

export default TaskList
