import React, { useState } from "react";
import { useSelector } from "react-redux";
import { NavLink } from "react-router-dom";
import { store } from "../../Redux/store";
import Form from "../../Components/Form/Form";
import classes from './LogIn.module.scss'



function LogIn() {

    const state = useSelector(store => store.users)
    console.log(state);

    const [loginValue, loginHandler] = useState('')
    const [passwordValue, passwordHandler] = useState('')



    const formAtributes = [
        {
            type: 'text',
            id: 'login',
            ph: 'Введите ваш логин',
            label: 'Логин',
            value: loginValue,
            onChange: (event) => loginHandler(event.target.value)
        },
        {
            type: 'password',
            id: 'pass',
            ph: 'Введите ваш пароль',
            label: 'Пароль',
            value: passwordValue,
            onChange: (event) => passwordHandler(event.target.value)
        }
    ]

    const btn = {
        name: 'Войти',
        onClick: () => resultReview()
    }




    function resultReview() {


        // state.find(user => {
        //     if(user.name === loginValue && user.password !== passwordValue){
        //         alert('Вы успешно авторизовались')
        //     }
        // })

        state.map((user) => {
            if(user.name !== loginValue){
                alert('Неверный логин')
                return
            }
            if(user.password !== passwordValue){
                alert('Неверный пароль')
                return
            }

            alert('Вы успешно авторизовались')

        })

    }




    return (
        <div className={classes.conteiner}>
            <h3>Добро Пожаловать</h3>
            <p>Войдите в свой аккаунт</p>
            <p className={classes.smallText}>Для тестового ознакомления,
            вы можете использовать "admin",
            в качестве логина и пароля</p>

            <Form a={formAtributes} btn={btn}>
            <span>Или вы можете <NavLink to='registration'>зарегистрироваться</NavLink></span>
            </Form>





            {/* <div className={classes.form}>
                <form>
                    <Input onChange={(event) => loginHandler(event.target.value)} type='text' id='login' ph='Введите ваш логин' label='Логин' value={loginValue} />
                    <Input onChange={(event) => passwordHandler(event.target.value)} type='password' id='pass' ph='Введите ваш пароль' label='Пароль' value={passwordValue} />

                    <div className={classes.btnLine}>
                    <Button onClick={() => resultReview()} name='Войти' />
                    <span>Или вы можете <NavLink to='registration'>зарегистрироваться</NavLink></span>
                    </div>
                </form>
            </div> */}
        </div>
    )
}



export default LogIn
